import data from "../../mocks/postData.json";
import { useContext } from "react";
import { SearchContext } from "../../context/SearchContext";
import { classess } from "./Posts.module.css";
export default function Posts() {
  const { inputText } = useContext(SearchContext);

  const filterData = data.filter((e) => {
    if (inputText == "") return e;
    if (inputText != "") return e.text.toLowerCase().includes(inputText);
  });
  return (
    <ul>
      {filterData.map((item) => (
        <li key={item.id} className={classess.listItem}>
          <h2 className="item-title">{item.title}</h2>
          <p className="item-text">{item.text}</p>
          <div className="item-details">
            <span className="item-author">By: {item.author}</span>
            <span className="item-date">Date: {item.date}</span>
            <span className="item-readTime">
              Read Time: {item.readTime} min
            </span>
          </div>
          <div className="item-stats">
            <span className="item-views">Views: {item.views}</span>
            <span className="item-liked">Likes: {item.liked}</span>
          </div>
        </li>
      ))}
    </ul>
  );
}
