import React, { useContext } from "react";
import { SearchContext } from "../../context/SearchContext";
import classess from "./Posts.module.css";
import Post from "./Post";
import data from "../../mocks/postData.json";

function Posts() {
  const { inputText } = useContext(SearchContext);

  const filterData = data.filter((e) => {
    if (inputText === "") return true;
    return e.text.toLowerCase().includes(inputText.toLowerCase());
  });

  return (
    <div className={classess.posts}>
      <div className={classes.RecentPostsText}>Recent Posts</div>
      <div className={classess.postsContainer}>
        <ul className={classess.listContainer}>
          {filterData.map((item) => (
            <Post key={item.id} item={item} />
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Posts;
