  const toggleOffCanvas = () => {
    setIsOffCanvasOpen(!isOffCanvasOpen);
    console.log("sd");
  };

  return (
    <OffCanvasContext.Provider value={{ isOffCanvasOpen, toggleOffCanvas }}>
      {children}
    </OffCanvasContext.Provider>
  );
};
