import data from "../../mocks/postData.json";
import { useContext } from "react";
import { SearchContext } from "../../context/SearchContext";
import classess from "./Posts.module.css";
import userIcon from "../../assets/userIcon.webp";
export default function Posts() {
  const { inputText } = useContext(SearchContext);

  const filterData = data.filter((e) => {
    if (inputText == "") return e;
    if (inputText != "") return e.text.toLowerCase().includes(inputText);
  });
  return (
    <div className={classess.posts}>
      <div className={classess.postsContainer}>
        <ul className={classess.listContainer}>
          {filterData.map((item) => (
            <li key={item.id} className={classess.listItem}>
              <div className={classess.itemDetails}>
                <img
                  src={userIcon}
                  alt="User Icon"
                  className={classess.userIcon}
                  width="50px"
                />
                <span className={classess.itemAuthor}>{item.author}</span>
                <div>
                  <span className="item-readTime">{item.readTime} min</span>
                  <span className="item-date"> {item.date}</span>
                </div>
              </div>

              <div className={classess.listContent}>
                <h2 className={classess.itemTitle}>{item.title}</h2>
                <p className={classess.itemText}>{item.text}</p>
              </div>
              <div className={classess.itemStats}>
                <span className="item-views">Views: {item.views}</span>
                <span className="item-liked">Likes: {item.liked}</span>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
